DROP TABLE IF EXISTS `attributes`;
CREATE TABLE IF NOT EXISTS `attributes` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` int(11) NOT NULL,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  `search` enum('true','false') DEFAULT 'false',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `attributes_categories`;
CREATE TABLE IF NOT EXISTS `attributes_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `type` enum('select','check','radio','input') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `categories` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL,
  `required` tinyint(1) NOT NULL,
  `search` enum('true','false') DEFAULT 'false',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `badges`;
CREATE TABLE IF NOT EXISTS `badges` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `from` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `to` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `type` enum('other','buyers','authors','referrals','system') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `sys_key` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `bulletin`;
CREATE TABLE IF NOT EXISTS `bulletin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `text` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  `send_to` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `send_id` int(11) NOT NULL,
  `readed` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `bulletin_emails`;
CREATE TABLE IF NOT EXISTS `bulletin_emails` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `firstname` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `lastname` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `bulletin_subscribe` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'true',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `bulletin_template`;
CREATE TABLE IF NOT EXISTS `bulletin_template` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `template` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `categories`;
CREATE TABLE IF NOT EXISTS `categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sub_of` int(11) NOT NULL,
  `meta_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_keywords` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL,
  `module` varchar(128) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `category_id` (`sub_of`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `categories_description`;
CREATE TABLE IF NOT EXISTS `categories_description` (
  `id` int(11) NOT NULL,
  `lid` int(11) NOT NULL DEFAULT '0',
  `name` varchar(255) DEFAULT NULL
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `collections`;
CREATE TABLE IF NOT EXISTS `collections` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `text` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `items` int(11) NOT NULL DEFAULT '0',
  `rating` int(11) NOT NULL DEFAULT '0',
  `votes` int(11) NOT NULL DEFAULT '0',
  `score` int(11) NOT NULL DEFAULT '0',
  `datetime` datetime NOT NULL,
  `public` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `collections_rates`;
CREATE TABLE IF NOT EXISTS `collections_rates` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `collection_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `rate` int(11) NOT NULL,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `collection_id` (`collection_id`,`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `contacts`;
CREATE TABLE IF NOT EXISTS `contacts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `issue_id` int(11) NOT NULL DEFAULT '0',
  `issue` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `short_text` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  `answer` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `answer_datetime` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `contacts_categories`;
CREATE TABLE IF NOT EXISTS `contacts_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `text` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `countries`;
CREATE TABLE IF NOT EXISTS `countries` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `countrycode`;
CREATE TABLE IF NOT EXISTS `countrycode` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `iso2` char(2) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `iso3` char(3) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `currency`;
CREATE TABLE IF NOT EXISTS `currency` (
  `currency_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ИД на валутата',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `symbol_left` varchar(12) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `symbol_right` varchar(12) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `code` varchar(3) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `decimal_place` int(1) NOT NULL,
  `value` float(15,8) NOT NULL,
  `decimal_point` char(3) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `thousand_point` char(3) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `status` int(1) NOT NULL,
  `date_added` datetime NOT NULL,
  `date_modified` datetime NOT NULL,
  PRIMARY KEY (`currency_id`),
  KEY `code` (`code`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `deposit`;
CREATE TABLE IF NOT EXISTS `deposit` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `deposit` float NOT NULL,
  `paid` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `datetime` datetime NOT NULL,
  `from_admin` tinyint(1) NOT NULL,
  `order_status_id` int(11) NOT NULL,
  `added` enum('true','false') DEFAULT 'false',
  `paid_from` varchar(45) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `from_admin` (`from_admin`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `deposit_sums`;
CREATE TABLE IF NOT EXISTS `deposit_sums` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `deposit` float NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `extensions`;
CREATE TABLE IF NOT EXISTS `extensions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `forum`;
CREATE TABLE IF NOT EXISTS `forum` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `status` enum('true','false') DEFAULT 'false',
  `order_index` int(11) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `forum_comments`;
CREATE TABLE IF NOT EXISTS `forum_comments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `thread_id` int(11) NOT NULL DEFAULT '0',
  `user_id` int(11) NOT NULL DEFAULT '0',
  `name` varchar(255) NOT NULL,
  `comment` text,
  `datetime` datetime DEFAULT NULL,
  `notify` enum('true','false') DEFAULT 'false',
  `reply_to` int(11) DEFAULT '0',
  `report_by` int(11) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `history`;
CREATE TABLE IF NOT EXISTS `history` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `action` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `transaction_id` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items`;
CREATE TABLE IF NOT EXISTS `items` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `thumbnail` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `theme_preview_thumbnail` varchar(255) NOT NULL,
  `theme_preview` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `main_file` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `main_file_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `categories` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `demo_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` float NOT NULL DEFAULT '0',
  `suggested_price` float NOT NULL,
  `sales` int(11) NOT NULL DEFAULT '0',
  `earning` float NOT NULL DEFAULT '0',
  `rating` int(11) NOT NULL DEFAULT '0',
  `votes` int(11) NOT NULL DEFAULT '0',
  `score` int(11) NOT NULL DEFAULT '0',
  `comments` int(11) NOT NULL DEFAULT '0',
  `free_request` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `free_file` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `weekly_from` date NOT NULL,
  `weekly_to` date DEFAULT NULL,
  `reviewer_comment` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  `status` enum('active','queue','unapproved','extended_buy','deleted') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'queue',
  `module` varchar(128) NOT NULL,
  `video_file` varchar(255) NOT NULL,
  `item_tags_string` text NOT NULL,
  `preview` int(1) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`,`categories`),
  KEY `module` (`module`),
  FULLTEXT KEY `name` (`name`,`description`),
  FULLTEXT KEY `item_tags_string` (`item_tags_string`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_attributes`;
CREATE TABLE IF NOT EXISTS `items_attributes` (
  `item_id` int(11) NOT NULL,
  `attribute_id` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `category_id` int(11) NOT NULL,
  KEY `item_id` (`item_id`,`attribute_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_collections`;
CREATE TABLE IF NOT EXISTS `items_collections` (
  `item_id` int(11) NOT NULL,
  `collection_id` int(11) NOT NULL,
  KEY `item_id` (`item_id`),
  KEY `collection_id` (`collection_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_comments`;
CREATE TABLE IF NOT EXISTS `items_comments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `owner_id` int(11) NOT NULL,
  `item_id` int(11) NOT NULL,
  `item_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `user_id` int(11) NOT NULL,
  `comment` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  `notify` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `reply_to` int(11) NOT NULL DEFAULT '0',
  `report_by` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `item_id` (`item_id`,`user_id`),
  KEY `owner_id` (`owner_id`),
  KEY `report_by` (`report_by`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_faqs`;
CREATE TABLE IF NOT EXISTS `items_faqs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `item_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `question` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `answer` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `item_id` (`item_id`,`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_prices`;
CREATE TABLE IF NOT EXISTS `items_prices` (
  `item_id` int(11) NOT NULL,
  `size_id` int(11) NOT NULL,
  `price` float NOT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `main_file` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `size` float NOT NULL,
  `width` int(11) NOT NULL,
  `height` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `item_id` (`item_id`),
  KEY `size_id` (`size_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_rates`;
CREATE TABLE IF NOT EXISTS `items_rates` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `item_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `rate` int(11) NOT NULL,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `collection_id` (`item_id`,`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_tags`;
CREATE TABLE IF NOT EXISTS `items_tags` (
  `item_id` int(11) NOT NULL,
  `tag_id` int(11) NOT NULL,
  `type` enum('usage','style','features') CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT 'usage',
  PRIMARY KEY (`item_id`,`tag_id`,`type`),
  KEY `tag_id` (`tag_id`),
  KEY `item_id` (`item_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `items_to_category`;
CREATE TABLE IF NOT EXISTS `items_to_category` (
  `item_id` int(11) NOT NULL,
  `categories` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  KEY `item_id` (`item_id`,`categories`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `language`;
CREATE TABLE IF NOT EXISTS `language` (
  `language_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(32) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '',
  `code` varchar(5) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `locale` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `locale_territory` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `image` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `sort_order` int(3) NOT NULL DEFAULT '0',
  `status` int(1) NOT NULL,
  PRIMARY KEY (`language_id`),
  KEY `name` (`name`),
  KEY `status` (`status`),
  KEY `sort_order` (`sort_order`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `language_keywords`;
CREATE TABLE IF NOT EXISTS `language_keywords` (
  `language_keywords_id` int(11) NOT NULL AUTO_INCREMENT,
  `keyword` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `module` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `key` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`language_keywords_id`),
  KEY `module` (`module`),
  KEY `key` (`key`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `language_translate`;
CREATE TABLE IF NOT EXISTS `language_translate` (
  `language_keywords_id` int(11) NOT NULL AUTO_INCREMENT,
  `language_id` int(11) NOT NULL,
  `keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  KEY `language_id` (`language_id`),
  KEY `language_keywords_id` (`language_keywords_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `membership`;
CREATE TABLE IF NOT EXISTS `membership` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL DEFAULT '0',
  `downloads` int(11) NOT NULL DEFAULT '0',
  `paid` enum('true','false') NOT NULL DEFAULT 'false',
  `max_downloads_cnt` int(11) NOT NULL DEFAULT '0',
  `datetime` datetime DEFAULT NULL,
  `amount` float NOT NULL DEFAULT '0',
  `order_status_id` int(11) NOT NULL DEFAULT '0',
  `added` enum('true','false') NOT NULL DEFAULT 'false',
  `valid` enum('true','false') NOT NULL DEFAULT 'true',
  `paid_from` varchar(45) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `membership_description`;
CREATE TABLE IF NOT EXISTS `membership_description` (
  `id` int(11) NOT NULL,
  `lid` int(11) NOT NULL,
  `description` varchar(100) NOT NULL
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `membership_sums`;
CREATE TABLE IF NOT EXISTS `membership_sums` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `price` float NOT NULL DEFAULT '0',
  `status` enum('true','false') NOT NULL DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  `max_items_cnt` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `notification_templates`;
CREATE TABLE IF NOT EXISTS `notification_templates` (
  `key` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `template` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `info` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  KEY `key` (`key`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `orders`;
CREATE TABLE IF NOT EXISTS `orders` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `order_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `owner_id` int(11) NOT NULL,
  `item_id` int(11) NOT NULL,
  `item_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` float NOT NULL,
  `receive` float NOT NULL DEFAULT '0',
  `datetime` datetime NOT NULL,
  `paid` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `paid_datetime` datetime NOT NULL,
  `extended` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `type` enum('buy','referal') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'buy',
  `currency_code` varchar(5) NOT NULL,
  `currency_value` decimal(15,8) NOT NULL,
  `domain` varchar(255) NOT NULL,
  `module` varchar(128) NOT NULL,
  `size_id` int(11) NOT NULL,
  `main_file` varchar(255) NOT NULL,
  `main_file_info` varchar(255) NOT NULL,
  `order_status_id` int(11) NOT NULL DEFAULT '0',
  `paid_from` varchar(45) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`,`item_id`),
  KEY `owner_id` (`owner_id`),
  KEY `order_id` (`order_id`),
  KEY `domain` (`domain`),
  KEY `size_id` (`size_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `pages`;
CREATE TABLE IF NOT EXISTS `pages` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sub_of` int(11) NOT NULL,
  `key` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_keywords` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `url` varchar(255) NOT NULL,
  `menu` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `footer` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `category_id` (`sub_of`),
  KEY `key` (`key`),
  KEY `footer` (`footer`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `pages_description`;
CREATE TABLE IF NOT EXISTS `pages_description` (
  `id` int(11) NOT NULL,
  `lid` int(11) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `text` longtext
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `percents`;
CREATE TABLE IF NOT EXISTS `percents` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `percent` int(11) NOT NULL,
  `from` int(11) NOT NULL,
  `to` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `quiz`;
CREATE TABLE IF NOT EXISTS `quiz` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `order_index` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `quiz_answers`;
CREATE TABLE IF NOT EXISTS `quiz_answers` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `quiz_id` int(11) NOT NULL,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `right` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `sizes`;
CREATE TABLE IF NOT EXISTS `sizes` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `size` float NOT NULL,
  `order_index` int(11) NOT NULL,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`),
  KEY `order_index` (`order_index`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `slider`;
CREATE TABLE IF NOT EXISTS `slider` (
  `id` int(6) NOT NULL AUTO_INCREMENT,
  `name` varchar(250) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `order_index` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `smiles`;
CREATE TABLE IF NOT EXISTS `smiles` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `code` varchar(64) NOT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `socials`;
CREATE TABLE IF NOT EXISTS `socials` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL,
  `link` varchar(255) DEFAULT NULL,
  `visible` enum('true','false') DEFAULT 'false',
  `order_index` int(11) NOT NULL DEFAULT '0',
  `photo` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `system`;
CREATE TABLE IF NOT EXISTS `system` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `key` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `value` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `system` tinyint(1) NOT NULL DEFAULT '0',
  `group` varchar(128) NOT NULL,
  `serialize` tinyint(1) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `group` (`group`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `tags`;
CREATE TABLE IF NOT EXISTS `tags` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `visible` enum('true','false') NOT NULL DEFAULT 'false',
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `temp_items`;
CREATE TABLE IF NOT EXISTS `temp_items` (
  `id` int(11) NOT NULL,
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `thumbnail` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `theme_preview_thumbnail` varchar(255) NOT NULL,
  `theme_preview` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `main_file` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `main_file_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `demo_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `suggested_price` float NOT NULL,
  `free_request` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `reviewer_comment` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  `module` varchar(128) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `module` (`module`),
  FULLTEXT KEY `name` (`name`,`description`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `temp_items_attributes`;
CREATE TABLE IF NOT EXISTS `temp_items_attributes` (
  `item_id` int(11) NOT NULL,
  `attribute_id` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `category_id` int(11) NOT NULL,
  KEY `item_id` (`item_id`,`attribute_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `temp_items_tags`;
CREATE TABLE IF NOT EXISTS `temp_items_tags` (
  `item_id` int(11) NOT NULL,
  `tag_id` int(11) NOT NULL,
  `type` enum('usage','style','features') CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT 'usage',
  PRIMARY KEY (`item_id`,`tag_id`,`type`),
  KEY `tag_id` (`tag_id`),
  KEY `item_id` (`item_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `temp_items_to_category`;
CREATE TABLE IF NOT EXISTS `temp_items_to_category` (
  `item_id` int(11) NOT NULL,
  `categories` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  KEY `item_id` (`item_id`,`categories`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `topbanner`;
CREATE TABLE IF NOT EXISTS `topbanner` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `html` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `background` varchar(7) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `from` date DEFAULT NULL,
  `to` date DEFAULT NULL,
  `close` enum('false','true') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'true',
  `views` int(11) NOT NULL DEFAULT '0',
  `clicks` int(11) NOT NULL DEFAULT '0',
  `width` int(11) NOT NULL,
  `height` int(11) NOT NULL,
  `cookie` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `users`;
CREATE TABLE IF NOT EXISTS `users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(30) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `password` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `firstname` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `lastname` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `featured_item_id` int(11) NOT NULL DEFAULT '0',
  `exclusive_author` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'true',
  `license` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'a:1:{s:8:"personal";s:8:"personal";}',
  `avatar` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `homeimage` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `firmname` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `profile_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `profile_desc` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `live_city` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `country_id` int(11) NOT NULL DEFAULT '0',
  `freelance` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `social` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `quiz` enum('false','true') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `deposit` float NOT NULL DEFAULT '0',
  `earning` float NOT NULL DEFAULT '0',
  `total` float NOT NULL DEFAULT '0',
  `sold` float NOT NULL DEFAULT '0',
  `items` int(11) NOT NULL DEFAULT '0',
  `sales` int(11) NOT NULL DEFAULT '0',
  `buy` int(11) NOT NULL DEFAULT '0',
  `rating` int(11) NOT NULL DEFAULT '0',
  `score` int(11) NOT NULL DEFAULT '0',
  `votes` int(11) NOT NULL DEFAULT '0',
  `referals` int(11) NOT NULL DEFAULT '0',
  `referal_money` float NOT NULL DEFAULT '0',
  `featured_author` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `register_datetime` datetime DEFAULT NULL,
  `last_login_datetime` datetime DEFAULT NULL,
  `ip_address` varchar(15) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` enum('waiting','banned','activate') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'waiting',
  `groups` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `remember_key` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `activate_key` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `referal_id` int(11) NOT NULL DEFAULT '0',
  `commission_percent` int(2) NOT NULL DEFAULT '0',
  `badges` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `author_status` varchar(50) DEFAULT NULL,
  `author_status_description` varchar(100) DEFAULT NULL,
  `user_site` varchar(255) NOT NULL,
  `item_note` enum('true','false') DEFAULT 'false',
  `daily` enum('true','false') DEFAULT 'false',
  `fb_id` bigint(20) DEFAULT '0',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `username` (`username`),
  KEY `status` (`status`),
  KEY `featured_item_id` (`featured_item_id`),
  KEY `referal_id` (`referal_id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `users_emails`;
CREATE TABLE IF NOT EXISTS `users_emails` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `from_id` int(11) NOT NULL,
  `from_email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `to_id` int(11) NOT NULL,
  `message` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `from_id` (`from_id`,`to_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `users_followers`;
CREATE TABLE IF NOT EXISTS `users_followers` (
  `user_id` int(11) NOT NULL,
  `follow_id` int(11) NOT NULL,
  PRIMARY KEY (`user_id`,`follow_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `users_referals_count`;
CREATE TABLE IF NOT EXISTS `users_referals_count` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `datetime` datetime NOT NULL,
  `referal_id` int(11) NOT NULL,
  `order_type` enum('sale','deposit','register','gast') NOT NULL,
  `referal_sum` float NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `referal_id` (`referal_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `users_status`;
CREATE TABLE IF NOT EXISTS `users_status` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `status` enum('freefile','featured') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `datetime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS `user_groups`;
CREATE TABLE IF NOT EXISTS `user_groups` (
  `ug_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `rights` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  PRIMARY KEY (`ug_id`)
) ENGINE=InnoDB;

DROP TABLE IF EXISTS `withdraw`;
CREATE TABLE IF NOT EXISTS `withdraw` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `amount` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `method` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `text` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `australian` enum('false','iam','iamnot') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `abn` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `acn` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `datetime` datetime NOT NULL,
  `paid` enum('true','false') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'false',
  `paid_datetime` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=8 ;














